import React, { useEffect, useState } from 'react'
import axios from  "axios"
import { Card, Col, Row } from 'react-bootstrap'


export const Dashboard = () => {

    const [image,setImage] = useState("")
    const [url,setUrl] = useState("")
    const [imgdata,setImgdata] = useState({
     url:"",
     userId:""
    })
    const [userId,setUserId] = useState("")
    const [arr,setArr] = useState([])


    useEffect(()=>{
        const userdata = JSON.parse(localStorage.getItem("nftuser"))
        console.log(userdata.user._id)
        setUserId(userdata.user._id)

        if(imgdata.url){
            fetch("http://localhost:5000/api/auth/upload",{
                method:"post",
                headers:{
                    "Content-Type":"application/json",
                }
            })
        }
    
    },[])

    const uploadImage = async()=>{
        console.log(image)
        let data = new FormData()
        let form = new FormData()
        data.append("file",image)
        data.append("upload_preset","nftimg")
        data.append("cloud_name","degu3b9yz")
   await fetch("https://api.cloudinary.com/v1_1/degu3b9yz/image/upload",{
            method:"POST",
            body:data
        }).then(res=>res.json())
        .then(data=>{
            const imgurl = data.url
            setUrl(data.url)
            arr.push(imgurl)
            imgdata.userId = userId
            
        }).catch(err=>{
            console.log(err)
        })
        
    }






console.log(imgdata)



return(
    <>
<input type= "file" onChange={(e)=>setImage(e.target.files[0])}/>

{
    arr.map((items,index)=>{
        return(
            <>
            <div style ={{width:"100%",position}}>

            <div style = {{width:"1000px",height:"500px"}}>

            <img style={{width:"30%"}} src = {items}/>
            
            </div>
            </div>

            </>
        )
    })
}

<input type = "submit" onClick={()=>uploadImage()}/>


<Card style={{ width: "30rem"}}>
