import React, { useEffect, useState } from 'react'
import axios from  "axios"


export const Dashboard = () => {

const [userdata,setuserdata] = useState([])
const [avatar, setavatar] = useState("");
const [name,setName] = useState("")
const [date,setDate] = useState("")


const updateProfileSubmit = async(e) => {
    e.preventDefault();
    const myForm = new FormData();
    
    myForm.set("avatar", avatar);
    console.log(myForm);
   const res =  await axios.post("http://localhost:5000/api/auth/upload")
   console.log(res)

  };

const handle = (e) => {
    const reader = new FileReader();
    reader.onload = () => {
      if (reader.readyState === 2) {
        // setavatarPreview(reader.result);
        setavatar(reader.result);
      }
    };

    reader.readAsDataURL(e.target.files[0]);
  };

useEffect(()=>{

    const res = localStorage.getItem("nftuser")
    setuserdata([JSON.parse(res)])
},[])

console.log(userdata)

  return (
<>
{
    userdata.map((items,index)=>{
        return(
    <>
    

    <h1>username:{items.user.username}</h1>
    <h2>dob:{new Date(items.user.dob).getUTCFullYear()}</h2>
    
    <form action="post">

        
    </form>
    <input
  type="file" className="profil-img"
 name="avatar"
 accept="image/*"onChange={handle}
                />
    
    </>
        )
    })
}

</>
  )
}
